import React,{ useState } from "react";
import "./Sidenav.css"

const Sidenav = () => {
 const [isOpen, changeClass] = useState(false);

const handleClick = () => {
  changeClass(!isOpen);
};
const linkClassName = isOpen ? 'open' : 'sidenav';
 return(<>
 <div className={linkClassName}>
  <a  className="closebtn" onClick={handleClick}> &times;</a>
  <a href="#">About</a>
  <a href="#">Services</a>
  <a href="#">Clients</a>
  <a href="#">Contact</a>
</div>

{/* Use any element to open the sidenav*/} 
<span style={{fontSize:'22px'}} onClick={handleClick}>&#9776;open</span>
 </>);
}

export default Sidenav
